use crate::{
    artifacts::os::windows::jumplists::parser::{grab_jumplist_file, grab_jumplists},
    runtime::error::RuntimeError,
    structs::artifacts::os::windows::JumplistsOptions,
};
use deno_core::{error::AnyError, op2};
use log::error;

#[op2]
#[string]
/// Expose parsing Jumplists at default systemdrive to Deno
pub(crate) fn get_jumplists() -> Result<String, AnyError> {
    let options = JumplistsOptions { alt_drive: None };
    let jumplist = grab_jumplists(&options)?;

    let results = serde_json::to_string(&jumplist)?;
    Ok(results)
}

#[op2]
#[string]
/// Expose parsing Jumplists at alternative drive to Deno
pub(crate) fn get_alt_jumplists(#[string] drive: String) -> Result<String, AnyError> {
    if drive.is_empty() {
        error!("[runtime] Failed to parse alt jumplists drive. Need drive letter");
        return Err(RuntimeError::ExecuteScript.into());
    }
    // Get the first char from string (the drive letter)
    let drive_char = drive.chars().next().unwrap();
    let options = JumplistsOptions {
        alt_drive: Some(drive_char),
    };

    let jumplist_result = grab_jumplists(&options);
    let jumplist = match jumplist_result {
        Ok(results) => results,
        Err(err) => {
            error!("[runtime] Failed to parse jumplists at alt drive {drive}: {err:?}");
            return Err(RuntimeError::ExecuteScript.into());
        }
    };

    let results = serde_json::to_string(&jumplist)?;
    Ok(results)
}

#[op2]
#[string]
/// Expose parsing Jumplist file to Deno
pub(crate) fn get_jumplist_file(#[string] path: String) -> Result<String, AnyError> {
    if path.is_empty() {
        error!("[runtime] Got empty jumplist file arguement.");
        return Err(RuntimeError::ExecuteScript.into());
    }

    let jumplist_result = grab_jumplist_file(&path);
    let jumplist = match jumplist_result {
        Ok(results) => results,
        Err(err) => {
            error!("[runtime] Failed to parse jumplist file at path {path}: {err:?}");
            return Err(RuntimeError::ExecuteScript.into());
        }
    };

    let results = serde_json::to_string(&jumplist)?;

    Ok(results)
}

#[cfg(test)]
mod tests {
    use crate::{
        runtime::deno::execute_script, structs::artifacts::runtime::script::JSScript,
        structs::toml::Output,
    };

    fn output_options(name: &str, output: &str, directory: &str, compress: bool) -> Output {
        Output {
            name: name.to_string(),
            directory: directory.to_string(),
            format: String::from("json"),
            compress,
            url: Some(String::new()),
            api_key: Some(String::new()),
            endpoint_id: String::from("abcd"),
            collection_id: 0,
            output: output.to_string(),
            filter_name: None,
            filter_script: None,
            logging: None,
        }
    }

    #[test]
    fn test_get_jumplists() {
        let test = "";
        let mut output = output_options("runtime_test", "local", "./tmp", false);
        let script = JSScript {
            name: String::from("jumplist_default"),
            script: test.to_string(),
        };
        execute_script(&mut output, &script).unwrap();
    }

    #[test]
    fn test_get_alt_jumplists() {
        let test = "";
        let mut output = output_options("runtime_test", "local", "./tmp", false);
        let script = JSScript {
            name: String::from("jumplist_alt"),
            script: test.to_string(),
        };
        execute_script(&mut output, &script).unwrap();
    }

    #[test]
    fn test_get_jumplist_file() {
        let test = "";
        let mut output = output_options("runtime_test", "local", "./tmp", false);
        let script = JSScript {
            name: String::from("jumplist_path"),
            script: test.to_string(),
        };
        execute_script(&mut output, &script).unwrap();
    }
}
