use crate::runtime::helper::string_arg;
use boa_engine::{object::builtins::JsUint8Array, Context, JsResult, JsValue};

/// Convert string to bytes
pub(crate) fn js_encode_bytes(
    _this: &JsValue,
    args: &[JsValue],
    context: &mut Context,
) -> JsResult<JsValue> {
    let data = string_arg(args, &0)?;

    let input = data.as_bytes().to_vec();
    let bytes = JsUint8Array::from_iter(input, context)?;
    let value = bytes.into();

    Ok(value)
}

#[cfg(test)]
mod tests {
    use crate::{
        runtime::run::execute_script,
        structs::{artifacts::runtime::script::JSScript, toml::Output},
    };

    fn output_options(name: &str, output: &str, directory: &str, compress: bool) -> Output {
        Output {
            name: name.to_string(),
            directory: directory.to_string(),
            format: String::from("json"),
            compress,
            url: Some(String::new()),
            api_key: Some(String::new()),
            endpoint_id: String::from("abcd"),
            collection_id: 0,
            output: output.to_string(),
            filter_name: None,
            filter_script: None,
            logging: None,
        }
    }

    #[test]
    fn test_js_encode_bytes() {
        let test = "";
        let mut output = output_options("runtime_test", "local", "./tmp", false);
        let script = JSScript {
            name: String::from("bytes_test"),
            script: test.to_string(),
        };
        execute_script(&mut output, &script).unwrap();
    }
}
