use crate::{artifacts::os::macos::bookmarks::parser::parse_bookmark, runtime::helper::bytes_arg};
use boa_engine::{Context, JsError, JsResult, JsValue, js_string};

/// Expose parsing bookmark data to `BoaJS`
pub(crate) fn js_bookmark(
    _this: &JsValue,
    args: &[JsValue],
    context: &mut Context,
) -> JsResult<JsValue> {
    let bytes = bytes_arg(args, 0, context)?;

    let book = match parse_bookmark(&bytes) {
        Ok(result) => result,
        Err(err) => {
            let issue = format!("Failed to get bookmark: {err:?}");
            return Err(JsError::from_opaque(js_string!(issue).into()));
        }
    };
    let results = serde_json::to_value(&book).unwrap_or_default();
    let value = JsValue::from_json(&results, context)?;

    Ok(value)
}

#[cfg(test)]
mod tests {
    use crate::{
        runtime::run::execute_script,
        structs::{artifacts::runtime::script::JSScript, toml::Output},
    };

    fn output_options(name: &str, output: &str, directory: &str, compress: bool) -> Output {
        Output {
            name: name.to_string(),
            directory: directory.to_string(),
            format: String::from("json"),
            compress,
            endpoint_id: String::from("abcd"),
            output: output.to_string(),
            ..Default::default()
        }
    }

    #[test]
    fn test_js_bookmark() {
        let test = "";
        let mut output = output_options("runtime_test", "local", "./tmp", true);

        let script = JSScript {
            name: String::from("bookmark_js"),
            script: test.to_string(),
        };
        execute_script(&mut output, &script).unwrap();
    }
}
